function submitScore(){event.preventDefault();const e=document.getElementById("timer").dataset.time,t={name:document.getElementById("name").value,time:e},n=`${window.location.pathname}/scores`,o=document.querySelector('meta[name="csrf-token"]').content;fetch(n,{method:"POST",headers:{"X-CSRF-Token":o,"Content-Type":"application/json"},body:JSON.stringify(t)}).then(e=>{if(e.ok)return e.json();throw new Error("Network response not ok.")}).then(()=>{console.log("Score created"),window.location.href=`${window.location.href}/scores`})["catch"](e=>{console.log(e.message,"Something bad happened")})}const timer=function(){function e(){n=!1}function t(e){const t=Math.floor(e/6e4),n=Math.floor(e%6e4/1e3);return`${t}:${n<10?"0"+n:n}`}let n=!0;const o=(new Date).getTime();let r=setInterval(function(){const e=(new Date).getTime()-o,c=document.getElementById("timer");c.innerText=t(e),c.dataset.time=e,n||clearInterval(r)},1e3);return{stopTimer:e,formatTime:t}}();